import { LogLevel } from '../logger/logger.interface';
export interface Config {
    /**
     * @description
     * Portal gateway API path.
     */
    apiRoot: string;
    /**
     * @description
     * Indicates whether debug mode is enabled.
     */
    debug: boolean;
    /**
     * @description
     * The configured log level.
     */
    logLevel: LogLevel;
    /**
     * @description
     * Name of the portal that current page belongs to.
     */
    portalName: string;
    /**
     * @description
     * Absolute path to the static resources.
     * Example: "file:///android_asset/backbase/static/items".
     */
    staticResourcesRoot: string;
    /**
     * @description
     * CX version, e.g. "5" or "6".
     */
    version: string;
}
